parameters.jsCode:
// Normaliza os dados recebidos do webhook
const inputData = $input.item.json;

// Extrai dados do booking
const booking = inputData.booking || inputData;
const settings = inputData.settings || {};

// Formata o telefone (remove caracteres não numéricos)
let phone = booking.guest_phone || '';
phone = phone.replace(/\D/g, '');

// Se não tiver DDI, adiciona 55 (Brasil)
if (phone.length === 11 && !phone.startsWith('55')) {
  phone = '55' + phone;
}

// Formata data e hora
const bookingDate = new Date(booking.booking_date);
const formattedDate = bookingDate.toLocaleDateString('pt-BR', {
  day: '2-digit',
  month: '2-digit',
  year: 'numeric'
});

const formattedWeekday = bookingDate.toLocaleDateString('pt-BR', {
  weekday: 'long'
});

// Formata horários (HH:MM)
const startTime = booking.start_time ? booking.start_time.substring(0, 5) : '';
const endTime = booking.end_time ? booking.end_time.substring(0, 5) : '';

// Dados do serviço
const serviceName = booking.services?.name || booking.service_name || 'Serviço';
const serviceDuration = booking.services?.duration_minutes || booking.duration_minutes || 60;
const servicePrice = booking.services?.price || booking.price || 0;

// Formata preço
const formattedPrice = new Intl.NumberFormat('pt-BR', {
  style: 'currency',
  currency: 'BRL'
}).format(servicePrice);

// Dados da agenda
const agendaTitle = booking.agendas?.title || booking.agenda_title || 'Agendamento';

// Tipo de evento
const eventType = inputData.event_type || 'new_booking';

// Mensagem personalizada de lembrete
const reminderMessage = settings.reminder_message || 'Olá! Este é um lembrete do seu agendamento.';

// Configurações da instância WhatsApp
const instance = {
  Server_url: 'https://webhook.gestorviana.com',
  Name: 'encha-letty',
  ApiKey: '4AB355A97A0C-4F79-A3E3-77E3C1306E27'
};

// Retorna dados normalizados
return {
  booking_id: booking.id || inputData.booking_id,
  guest_name: booking.guest_name,
  guest_phone: phone,
  guest_email: booking.guest_email,
  booking_date: booking.booking_date,
  formatted_date: formattedDate,
  formatted_weekday: formattedWeekday,
  start_time: startTime,
  end_time: endTime,
  service_name: serviceName,
  service_duration: serviceDuration,
  service_price: servicePrice,
  formatted_price: formattedPrice,
  agenda_title: agendaTitle,
  event_type: eventType,
  reminder_message: reminderMessage,
  notes: booking.notes || '',
  status: booking.status || 'pending',
  instance: instance
};

const data = $input.item.json;

const message = `❌ *Agendamento Cancelado*\n\n` +
  `Olá *${data.guest_name}*,\n\n` +
  `Informamos que seu agendamento foi *cancelado*.\n\n` +
  `📋 *Detalhes do agendamento cancelado:*\n` +
  `📅 Data: ${data.formatted_date} (${data.formatted_weekday})\n` +
  `🕐 Horário: ${data.start_time} às ${data.end_time}\n` +
  `💼 Serviço: ${data.service_name}\n\n` +
  `Se desejar fazer um novo agendamento, ficaremos felizes em atendê-lo! 😊\n\n` +
  `Entre em contato conosco quando quiser.`;

return {
  phone: data.guest_phone,
  message: message,
  instance: data.instance
};

